/**
 * 手机号校验 参数：手机号
 */
function validateMobileNum(mobile) {
	if (mobile.length != 11) {
		return false;
	}
	var myreg = /^((13|15|18|14|17)+\d{9})$/;
	if (!myreg.test(mobile)) {
		return false;
	}

	return true;
}
/**
 * 是否数字 false 全是数字；true 含有字符
 */
function validateNumber(number) {
	var re = /^-?[1-9]*(\.\d+)?$|^-?0(\.\d+)?$/;
	if (!re.test(number)) {
		return false;
	} else {
		return true;
	}
}

function checkInput(strings, type) {
	var regBox = {
		regEmail : /^([a-z0-9_\.-]+)@([\da-z\.-]+)\.([a-z\.]{2,6})$/,// 邮箱
		regName : /^[a-z0-9_-]{3,16}$/,// 用户名
		regMobile : /^0?1[3|4|5|7|8][0-9]\d{8}$/,// 手机
		regTel : /^0[\d]{2,3}-[\d]{7,8}$/,
		regId : /^[1-9]\d{5}[1-9]\d{3}((0\d)|(1[0-2]))(([0|1|2]\d)|3[0-1])((\d{4})|\d{3}[x|X])$/
	};
	return regBox[type].test(strings);
}

/**
 * 
 * 当对象是否为空、空格、null、undifined时，返回空串
 * 
 * @param param
 * @returns '' or param
 */
function getBlankString(param) {
	if (!param || param == null || param == '' || param=='NaN' || param=='null' || typeof (param) == undefined) {
		return '--';
	}
	return param;
}
/**
 * 
 * 获取URL 传递参数
 * 
 * @param para
 * @returns 参数
 */
function getQuery(para) {
	var reg = new RegExp("(^|&)" + para + "=([^&]*)(&|$)");
	var r = window.location.search.substr(1).match(reg);
	if (r != null) {
		return unescape(r[2]);
	}
	return null;
}